<script setup>
import {ref} from 'vue'
import ChatMessage from '@/components/ChatMessage.vue';

const messageText = ref('je suis un message');
const messageList = ref([]);

function addMessage(){
    if(! messageText.value){
        return;
    }

    messageList.value.push({
        text: messageText.value,
        date: new Date(),
        user: {
            username: 'PetitGens',
            avatarUrl: 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/654c7554-888a-4fff-bc61-5785cc9b7fa9/de6e47n-dee882ce-0fe5-4ea2-9bc9-c06755e6bf70.png/v1/fill/w_894,h_894/shiny_eevee__dp_sprite__by_lazoofficial_de6e47n-pre.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..GaDuq0RJtLLlLp-cWnHgGCn8-j203qfxjAvVrvZkMZY'
        }
    });
    messageText.value = '';
}

</script>

<template>
    <div v-for="(message, index) in messageList" class="p-4" :key="index">
        <chat-message :message="message"></chat-message>
    </div>
    <div class="flex align-center p-2">
        <textarea v-model="messageText" name="message" id="message" rows="1" class="text-black rounded-md"></textarea>
        <button @click="addMessage" class="p-2 bg-blue-600 rounded-md ml-3">Envoyer</button>
    </div>
</template>